import React, { useEffect, useState } from 'react'
import MainLayout from '../layout/MainLayout'
import { useLocation } from 'react-router-dom';
import { MDBCol, MDBContainer, MDBRow, MDBCard, MDBCardTitle, MDBCardText, MDBCardBody, MDBCardImage, MDBBtn } from 'mdb-react-ui-kit';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import Swal from 'sweetalert2';


function LandingPage() {
    const location = useLocation();
    const user = location.state.id;
    const [loading,setLoading] = useState(false);
    const [data,setData] = useState([]);

    useEffect(()=>{
        getUserdata()
    },[])


    const getUserdata = async() =>{
        // setLoading(true);

        try {
            const response = await axios.get(`http://localhost:5000/api/v2/user/users/${user}`);
            console.log("collected info:",response.data);
            toast.error('User Data fetched');
            console.log('User data collected')
            setData(response.data);
            // setLoading(false);

        } catch (error) {
            console.log(error);
            // setLoading(false);

        }
    }
    return (
        <MainLayout>
            <div className="vh-100 rounded-3xl" style={{ backgroundColor: '#9de2ff' }}>
                <MDBContainer>
                    <MDBRow className="justify-content-center">
                        <MDBCol md="9" lg="7" xl="5" className="mt-5">
                            <MDBCard style={{ borderRadius: '15px' }}>
                                <MDBCardBody className="p-4">
                                    <div className="d-flex text-black">
                                        <div className="flex-shrink-0">
                                            <MDBCardImage
                                                style={{ width: '180px', borderRadius: '10px' }}
                                                src='https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-profiles/avatar-1.webp'
                                                alt='Generic placeholder image'
                                                fluid />
                                        </div>
                                        <div className="flex-grow-1 ms-3">
                                            <MDBCardTitle className="font-bold">{location.state.id}</MDBCardTitle>
                                            <MDBCardText>Senior Journalist</MDBCardText>

                                            <div className="d-flex justify-content-start rounded-3xl p-5 mb-2 text-white"
                                                style={{ backgroundColor: '#000000' }}>
                                                <div>
                                                    <p className="small text-muted mb-1 font-bold text-2xl">Profile Details</p>
                                                    <p className="mb-0">41</p>
                                                </div>
                                                <div className="px-3">
                                                    <p className="small text-muted mb-1 text-xl">Email</p>
                                                    <p className="mb-0">{data.email}</p>
                                                </div>
                                                <div>
                                                    <p className="small text-muted mb-1">Rating</p>
                                                    <p className="mb-0">8.5</p>
                                                </div>
                                            </div>
                                            <div className="d-flex pt-1 text-center">
                                                <form action="">
                                                    <button className="bg-indigo-500 justify-center items-center text-white p-3 rounded-2xl w-[200px]">Subscribe</button>
                                                </form>

                                            </div>
                                        </div>
                                    </div>
                                </MDBCardBody>
                            </MDBCard>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        </MainLayout>
    )
}

export default LandingPage
